class_id = []
for i in range(4):
    id = [0] * 4
    id[i] = 1
    class_id.append(id)

IMG_WIDTH = 320
IMG_HEIGHT = 320


face_cascade = cv.CascadeClassifier()

if not face_cascade.load("./cascades/haarcascade_frontalface_alt.xml"):
    print("Fuck")
    exit(0)

def find_face(image):
    grey = cv.cvtColor(img, cv.COLOR_BGR2GRAY)
    grey = cv.equalizeHist(grey)

    faces = face_cascade.detectMultiScale(grey)
    center = (550//2,0)
    for (x,y,w,h) in faces:
        center = (x + w//2, y + h//2)
    
    return center


def crop_face(image, loc):
    x,y = loc
    min_x = min(max(x - IMG_WIDTH//2, 0), len(image[0] - IMG_WIDTH))
    max_x = min(max(x + IMG_WIDTH//2, IMG_WIDTH), len(image[0]))
    min_y = min(max(y - IMG_HEIGHT//2, 0), len(image) - IMG_HEIGHT)
    max_y = min(max(y + IMG_HEIGHT//2, IMG_HEIGHT), len(image))
    return image[min_y:max_y,min_x:max_x], (min_x, max_x, min_y, max_y)

def crop_n_save_label(name,out,frame):
    truth = scipy.io.loadmat("./clothing-co-parsing/annotations/pixel-level/{}.mat".format(name))["groundtruth"]
    pixels = []
    min_x, max_x, min_y, max_y = frame
    for c in range(min_y, max_y):
        row = []
        for r in range(min_x,max_x):
            row.append(class_id[labels_to_classes[truth[c][r]]])
        pixels.append(row)

    # print("doneA")
    pixels = np.array(pixels, dtype=np.uint8)
    # print(pixels)
    # print("doneB")
    pixels = np.reshape(pixels, (-1,(IMG_HEIGHT)*4))
    # print(w*4)
    # print("doneC")
    pd.DataFrame(pixels).to_csv("./clothing-co-parsing/cropped-labels/{}.csv".format(out), header=None, index=None)

skip = 0

for i in range(1, 1003):
    print(".", end="")
    if(i % 100 == 0):
        print(f" {i}")
    if to_be_skipped[i] == 1:
        skip+=1
