    img = cv.imread(f"./clothing-co-parsing/photos/{i:04d}.jpg")
    loc = find_face(img)
    # cv.ellipse(img, loc, (2,2), 0, 0, 360, (255,0,0))
    img, frame = crop_face(img, loc)
    crop_n_save_label(f"{i:04d}",f"{i-skip:04d}", frame)
    cv.imwrite(f"./clothing-co-parsing/cropped/{i-skip:04d}.jpg", img)


